import React, { useState, useEffect } from "react";
import SProduct from './SingleProduct.css';
import items from "../items.json";
import { Link, useParams } from "react-router-dom";

export default function SingleProduct() {
  let [singleProduct, setSingleProduct] = useState([])
  const { name } = useParams()

  useEffect(() => {
    const findProduct = () => {
      const newProduct = items.products.find((product) => product.name === name)
      setSingleProduct(newProduct)
      console.log(newProduct)
    }

    findProduct()
  }, [name])

  return (
    <div className="Product">
        <img className="pic" src={singleProduct.picture} alt={singleProduct.name} />
        <h1>{name}</h1>
        <p className="">{singleProduct.price}</p>
        <button>
          <Link to="/">&larr; Volver</Link>
        </button>
    </div>
  )
}
