import './HomePage.css';
import items from './items.json';
import { useState, useContext } from "react"
import { Link } from "react-router-dom"

function HomePage() {
  const [items] = useState(data)

  return (
    <div className="HomePage">
      <div className="boxes">
      {items.products.map(({ name, picture, category }) => (
          <article
            key={name}
            className="grid grid-cols-1 gap-5 md:grid-cols-2 md:place-items-center lg:gap-10 xl:gap-20"
          >
            <div>
              <picture>
                <source media="(min-width: 768px)" srcSet={picture} />
                <img src={picture} alt={name} />
              </picture>
            </div>

            <div>
              <h2 className="font-bold text-4xl mb-5 text-white">{name}</h2>
              <p className="text-slate-300 mb-10">{`${category.substring(
                0,
                200
              )}...`}</p>
              <ul className="flex items-center justify-between">
                <li>
                  <Link
                    to={`/${name}`}
                    className="border-2 border-white py-2 px-4 text-white"
                  >
                    More Details
                  </Link>
                </li>
              </ul>
            </div>
          </article>
        ))}
        {/* {items.map( response => {
          return(
            <div className="box" key={response.name}>
                <img src={response.picture} alt={response.name}/>
                <div>
                  <h1>{response.name}</h1>
                  <h2>{response.price}</h2>
                </div>
            </div>
          )
        })} */}
      </div>
    </div>
  );
}

export default HomePage;
